package Class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//storing and representing numeric values;
		byte box1=10;
		short box2=13437;
		int box3=100000;
		long box4=1000000;
		//storing and representing decimals;	
		float variable1=1.99F;
		double variable2=99.99;
		//storing a single character;		
		char container='c';
		char dollar= '$';
		//storing yes or no values;  
		boolean sleepy=true;
		boolean energetic=false;
		 
		System.out.println(box1);   
		System.out.println(box2);
		System.out.println(box3);
		System.out.println(box4);
		System.out.println(variable1);
		System.out.println(variable2);
		System.out.println(sleepy);
		System.out.println(energetic);  
		
		
	}

}
