package Class07_Loop;

public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=1;
		while(num<=3) {
			System.out.println("I'm a while loop");
			num++;
		}
		System.out.println("-------------------------------");
		
		int num1=1;
		do {
			System.out.println("I'm a do while loop");
			num1++;
		}while(num1<=3);
		
	}

}
