package Class07_Loop;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int time=9;
	if(time<12) { 
		System.out.println("Good morning if statement");
	} /*
	infinite loop
	while(time<12)
	System.out.println("Good morning");
	**/
	
	while(time<12) { 
		System.out.println("Good morning while loop");
		time++;
	}
		
	}}


